class Solution:
    # Time complexity: O(n)
    # Space complexity: O(n)
    def longestValidParentheses(self, s: str) -> int:
        n = len(s)

        if n < 2:
            return 0
        # Cache: matrix n x 2 where the ith row is a pair [diff, longest_valid_parentheses].
        #
        # - diff is the difference between number of opened and closed parentheses in the substring s[i:].
        # - longest_valid_parentheses is the longest valid parentheses sequence starting at s[i].
        self.cache = [[0, 0] for _ in range(n)]

        max_len = 0

        for i in range(n):
            j = n - i - 1
            self.cache[j] = self.lvp_starting_at(j, s)
            max_len = max(max_len, self.cache[j][1])

        return max_len

    def lvp_starting_at(self, pos, s):
        n = len(s)

        lvp = 0
        diff = 0

        i = pos

        while (i < n) and (diff >= 0):
            cached_diff, cached_lvp = self.cache[i]

            if cached_lvp != 0:
                if diff == 0:
                    lvp += cached_lvp

                i += cached_lvp
                continue

            if cached_diff > 0:
                diff += cached_diff
                i += cached_diff
                continue

            diff += (1 if s[i] == "(" else -1)

            if diff == 0:
                lvp = i + 1 - pos

            i += 1

        return [diff, lvp]


# s = "(((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((((